import { useState } from 'react';
import style from'./App.css';
import axios from './components/Card/Card.jsx';
import data from './components/Card/Card.jsx';
import Cards from './components/Cards/Cards.jsx';
import Nav from './components/NavBar/NavBar.jsx';
import onClose from './components/Card/Card.jsx';
//import Card from './components/Card/Card.jsx';

function App() {

   const [characters, setCharacters] = useState([]);
   const onSearch = (id) => {
      axios(`${URL}/${id}`)
      if (data.name)  {
         setCharacters((oldChars) => [...oldChars, data]);
      } else {
         window.alert('Porfavor revise el ID ingresado');
      }
   // const onClose = (id) => {
   //    setCharacters(characters.filter((char) => char.id != id))
  // };
   }
   return (
      <div>
      <div className='App' style={{ padding: "25px "}}>
         <div className={style.nav}>
         <Nav onSearch={onSearch} />
         </div>
         <div>
         <Cards characters={characters} onClose={onClose} />
         </div>
         </div>
         <Cards
         id={characters.id}
         name={characters.name}
         status={characters.status}
         species={characters.species}
         gender={characters.gender}
         origin={characters.origin.name}
         image={characters.image}
         onClose={() => window.alert('Estas seguro que quieres borrar el personaje?')}/>
         </div>
   )

}

export default App;
